mapper.update:
update activity
set

    name = #{name}


    ,start_time = #{startTime,jdbcType=TIMESTAMP}


    ,end_time = #{endTime,jdbcType=TIMESTAMP}


    ,college_id = #{college_id}


    ,director_id = #{director_id}


    ,status_id = #{status_id}


    ,position_id = #{name}

where id=#{id,jdbcType=INTEGER}
